// src/pages/CallPage.jsx
import React, { useState, useEffect } from "react";
import useWebRTC from "../hooks/useWebRTC";
import PrecallSplash from "../components/PrecallSplash";
import Chat from "../components/Chat";
import MicIcon from "@mui/icons-material/Mic";
import MicOffIcon from "@mui/icons-material/MicOff";
import CallEndIcon from "@mui/icons-material/CallEnd";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";

// --- Styles
const styles = {
  callApp: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
    background: '#202124',
    fontFamily: '"Google Sans", sans-serif',
    color: '#e8eaed',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '0.5rem 1rem',
    background: '#303134',
  },
  meetingId: {
    fontSize: '1rem',
    fontWeight: 500,
  },
  headerBtn: {
    background: 'transparent',
    border: 'none',
    color: '#e8eaed',
    cursor: 'pointer',
    padding: '0.4rem',
    borderRadius: '50%',
  },
  statusIndicator: {
    textAlign: 'center',
    padding: '0.5rem 1rem',
    color: '#1a73e8',
    fontWeight: 500,
    background: 'rgba(26,115,232,0.1)',
    margin: '0 1rem',
    borderRadius: '8px',
  },
  main: {
    flex: 1,
    display: 'grid',
    gridTemplateColumns: '1fr 1px 1fr 320px',
    gridTemplateRows: '1fr',
    gap: '1rem',
    padding: '1rem',
    alignItems: 'center',
  },
  divider: {
    background: 'rgba(255,255,255,0.2)',
    width: '1px',
    height: '50%',
    justifySelf: 'center',
  },
  card: {
    background: 'transparent',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '0.5rem',
    position: 'relative',
  },
  name: {
    fontSize: '1rem',
  },
  controls: {
    padding: '1rem',
    display: 'flex',
    justifyContent: 'center',
    gap: '2rem',
    background: '#303134',
  },
  footerBtn: {
    background: 'transparent',
    border: 'none',
    color: '#e8eaed',
    cursor: 'pointer',
    padding: '0.75rem',
    borderRadius: '50%',
  },
  audioPlayer: {
    display: 'none',
  },
  chatPanel: {
    background: '#202124',
    borderRadius: '12px',
    padding: '1rem',
  },
};

export default function CallPage({ callId }) {
  const params = new URLSearchParams(window.location.search);
  const isInitiator =
    params.get("initiator") === "true" ||
    sessionStorage.getItem("isInitiator") === "true";

  const [localStream, setLocalStream] = useState(null);
  const [splashDone, setSplashDone] = useState(false);
  const [chatOpen, setChatOpen] = useState(false);
  const [muted, setMuted] = useState(false);

  const {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    isChannelOpen,
  } = useWebRTC(callId, {
    start: splashDone && !!localStream,
    isInitiator,
    localStream,
  });

  const handleJoin = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setLocalStream(stream);
      setSplashDone(true);
    } catch (err) {
      console.error("Permission micro refusée :", err);
      alert("Vous devez autoriser le micro pour rejoindre l’appel.");
    }
  };

  if (!splashDone) {
    return <PrecallSplash onReady={handleJoin} />;
  }

  return (
    <div style={styles.callApp} className={chatOpen ? "chat-open" : ""}>
      <header style={styles.header}>
        <span style={styles.meetingId}>ID : {callId}</span>
        <button style={styles.headerBtn} onClick={() => setChatOpen((o) => !o)}>
          <ChatBubbleOutlineIcon />
        </button>
      </header>

      {isInitiator && <div style={styles.statusIndicator}>{status}</div>}

      <main style={styles.main}>
        <div style={styles.card}>
          <div style={styles.name}>Vous</div>
          <button
            style={styles.footerBtn}
            onClick={() => { toggleMute(); setMuted((m) => !m); }}
          >
            {muted ? <MicOffIcon /> : <MicIcon />}
          </button>
        </div>
        <div style={styles.divider} />
        <div style={styles.card}>
          <div style={styles.name}>Interlocuteur</div>
        </div>

        {chatOpen && (
          <aside style={styles.chatPanel}>
            <Chat
              messages={chatMessages}
              onSend={sendMessage}
              isChannelOpen={isChannelOpen}
            />
          </aside>
        )}
      </main>

      <footer style={styles.controls}>
        <button style={styles.footerBtn} onClick={hangUp}>
          <CallEndIcon />
        </button>
      </footer>

      <audio ref={remoteAudioRef} autoPlay style={styles.audioPlayer} />
    </div>
  );
}
