// src/pages/CallPage.jsx
import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Grid, Paper, Drawer, Box, Button, Avatar } from '@mui/material';
import MicIcon from '@mui/icons-material/Mic';
import MicOffIcon from '@mui/icons-material/MicOff';
import CallEndIcon from '@mui/icons-material/CallEnd';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

import useWebRTC from '../hooks/useWebRTC';
import PrecallSplash from '../components/PrecallSplash';
import Chat from '../components/Chat';

export default function CallPage({ callId }) {
  const params = new URLSearchParams(window.location.search);
  const isInitiator = params.get('initiator') === 'true';

  const [localStream, setLocalStream] = useState(null);
  const [splashDone, setSplashDone] = useState(false);
  const [chatOpen, setChatOpen] = useState(false);
  const [muted, setMuted] = useState(false);

  const {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    isChannelOpen,
  } = useWebRTC(callId, {
    start: splashDone && !!localStream,
    isInitiator,
    localStream,
  });

  const handleJoin = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setLocalStream(stream);
      setSplashDone(true);
    } catch (err) {
      console.error('Autorisation micro refusée:', err);
      alert('Vous devez autoriser l\u2019accès au micro pour rejoindre l\u2019appel.');
    }
  };

  if (!splashDone) {
    return <PrecallSplash onReady={handleJoin} />;
  }

  // Copier le lien d'invitation
  const handleCopy = () => {
    navigator.clipboard.writeText(window.location.href);
  };

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* AppBar */}
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Conversation privée & sécurisée
          </Typography>
          <IconButton color="inherit" onClick={handleCopy}>
            <ContentCopyIcon />
          </IconButton>
          <IconButton color="inherit" onClick={() => setChatOpen(o => !o)}>
            <ChatBubbleOutlineIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Main area */}
      <Grid container sx={{ flexGrow: 1, p: 2, gap: 2 }}>        
        <Grid item xs={12} md={8} container spacing={2} justifyContent="center">
          {/* Local User Card */}
          <Grid item xs={6} sm={4} md={3}>
            <Paper elevation={3} sx={{ p: 2, textAlign: 'center' }}>
              <Avatar sx={{ width: 64, height: 64, mx: 'auto', mb: 1 }}>V</Avatar>
              <Typography variant="subtitle1">Vous</Typography>
              <IconButton sx={{ mt: 1 }} onClick={() => { toggleMute(); setMuted(m => !m); }}>
                {muted ? <MicOffIcon color="error" /> : <MicIcon color="action" />}
              </IconButton>
            </Paper>
          </Grid>

          {/* Remote Peer Card */}
          <Grid item xs={6} sm={4} md={3}>
            <Paper elevation={3} sx={{ p: 2, textAlign: 'center' }}>
              <Avatar sx={{ width: 64, height: 64, mx: 'auto', mb: 1 }}>I</Avatar>
              <Typography variant="subtitle1">Interlocuteur</Typography>
              {/* uniquement indicateur muet du peer */}
            </Paper>
          </Grid>
        </Grid>

        {/* Chat Drawer */}
        <Drawer anchor="right" open={chatOpen} onClose={() => setChatOpen(false)}>
          <Box sx={{ width: 300, p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Chat sécurisé
            </Typography>
            <Chat
              messages={chatMessages}
              onSend={sendMessage}
              isChannelOpen={isChannelOpen}
            />
          </Box>
        </Drawer>
      </Grid>

      {/* Footer controls */}
      <Box sx={{ p: 1, borderTop: '1px solid #ccc', textAlign: 'center' }}>
        <Button
          variant="contained"
          color="error"
          startIcon={<CallEndIcon />}
          onClick={hangUp}
        >
          Raccrocher
        </Button>
      </Box>

      {/* Audio element */}
      <audio ref={remoteAudioRef} autoPlay hidden />
    </Box>
  );
}
