import { useEffect, useRef, useState } from "react";

export default function useWebRTC(
  callId,
  { timeout = 30000, start = true, isInitiator = false }
) {
  const [status, setStatus] = useState("waiting");
  const statusRef = useRef(status);
  useEffect(() => { statusRef.current = status; }, [status]);

  const wsRef = useRef();
  const pcRef = useRef();
  const dataChannelRef = useRef();
  const remoteAudioRef = useRef();
  const localStreamRef = useRef();

  const [chatMessages, setChatMessages] = useState([]);
  const [localSpeaking, setLocalSpeaking] = useState(false);
  const [remoteSpeaking, setRemoteSpeaking] = useState(false);
  const [remoteMuted, setRemoteMuted] = useState(false);

  useEffect(() => {
    if (!start) return;

    const serverUrl = process.env.REACT_APP_SERVER_URL;
    wsRef.current = new WebSocket(
      `${serverUrl.replace(/^http/, "ws")}?roomId=${callId}`
    );
    wsRef.current.onopen = () =>
      console.log("WS open, readyState =", wsRef.current.readyState);
    wsRef.current.onclose = () => console.log("WS closed");

    wsRef.current.onmessage = async ({ data }) => {
      let msg;
      try {
        msg = JSON.parse(data);
      } catch {
        return;
      }
      switch (msg.type) {
        case "room-status":
          if (msg.peers === 2 && statusRef.current === "waiting") {
            await initiateCall(isInitiator);
          }
          break;
        case "offer":
          console.log("â† offer reÃ§u");
          await pcRef.current.setRemoteDescription(msg.offer);
          {
            const answer = await pcRef.current.createAnswer();
            await pcRef.current.setLocalDescription(answer);
            wsRef.current.send(JSON.stringify({ type: "answer", answer }));
          }
          break;
        case "answer":
          console.log("â† answer reÃ§ue");
          await pcRef.current.setRemoteDescription(msg.answer);
          break;
        case "candidate":
          console.log("â† candidate reÃ§ue");
          await pcRef.current.addIceCandidate(msg.candidate);
          break;
        case "peer-left":
          setStatus("peer-left");
          break;
        case "call-ended":
          setStatus("ended");
          break;
      }
    };

    const timer = setTimeout(() => {
      if (statusRef.current !== "connected") setStatus("timeout");
    }, timeout);

    return () => {
      clearTimeout(timer);
      wsRef.current?.close();
      pcRef.current?.close();
    };
  }, [callId, start, isInitiator, timeout]);

  async function initiateCall(isInitiator) {
    console.log("initiateCall, initiator =", isInitiator);
    setStatus("connecting");
    pcRef.current = new RTCPeerConnection({
      iceServers: [{ urls: process.env.REACT_APP_STUN_SERVER }],
    });

    localStreamRef.current = await navigator.mediaDevices.getUserMedia({
      audio: true,
    });
    // â€¦ (speech detection identique)
    localStreamRef.current
      .getTracks()
      .forEach((t) => pcRef.current.addTrack(t, localStreamRef.current));

    pcRef.current.ontrack = ({ streams: [stream] }) => {
      remoteAudioRef.current.srcObject = stream;
      // â€¦ (dÃ©tection remoteSpeaking / remoteMuted)
    };

    // DataChannel
    if (isInitiator) {
      dataChannelRef.current = pcRef.current.createDataChannel("chat");
      setupDataChannel();
    } else {
      pcRef.current.ondatachannel = ({ channel }) => {
        dataChannelRef.current = channel;
        setupDataChannel();
      };
    }

    pcRef.current.onicecandidate = ({ candidate }) => {
      if (candidate) {
        console.log("â†’ envoi candidate");
        wsRef.current.send(JSON.stringify({ type: "candidate", candidate }));
      }
    };

    if (isInitiator) {
      const offer = await pcRef.current.createOffer();
      await pcRef.current.setLocalDescription(offer);
      console.log("â†’ envoi offer");
      wsRef.current.send(JSON.stringify({ type: "offer", offer }));
    }

    setStatus("connected");
  }

  function setupDataChannel() {
    dataChannelRef.current.onopen = () =>
      console.log("ðŸ”¥ DataChannel open");
    dataChannelRef.current.onmessage = ({ data }) => {
      console.log("â€¹â€¹ DC message reÃ§u â€ºâ€º", data);
      setChatMessages((prev) => [...prev, { sender: "peer", text: data }]);
    };
  }

  function sendMessage(text) {
    console.log(
      "â†’ sendMessage, DC state=",
      dataChannelRef.current?.readyState,
      "text=",
      text
    );
    if (dataChannelRef.current?.readyState === "open") {
      dataChannelRef.current.send(text);
      setChatMessages((prev) => [...prev, { sender: "local", text }]);
    }
  }

  function toggleMute() {
    const t = localStreamRef.current?.getAudioTracks()[0];
    if (t) t.enabled = !t.enabled;
  }

  function hangUp() {
    if (isInitiator) {
      wsRef.current.send(JSON.stringify({ type: "end-call" }));
    }
    setStatus("ended");
    localStreamRef.current?.getTracks().forEach((t) => t.stop());
    setTimeout(() => {
      wsRef.current?.close();
      pcRef.current?.close();
    }, 100);
  }

  return {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    localSpeaking,
    remoteSpeaking,
    remoteMuted,
  };
}
