import React, { useState, useEffect } from 'react';
import useWebRTC from '../hooks/useWebRTC';
import Chat from './Chat';

export default function CallPage({ callId }) {
  const isInitiator = sessionStorage.getItem('isInitiator') === 'true';
  const [accepted, setAccepted] = useState(isInitiator);
  const { remoteAudioRef, status, chatMessages, sendMessage, toggleMute, hangUp } =
    useWebRTC(callId, { start: accepted, timeout: 30000 });
  const [msg, setMsg] = useState('');

  // Sons de connexion / déconnexion
  function playBeep() {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const o = ctx.createOscillator();
    o.type = 'sine';
    o.frequency.value = 440;
    o.connect(ctx.destination);
    o.start();
    setTimeout(() => o.stop(), 200);
  }
  function playHangup() {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const o = ctx.createOscillator();
    o.type = 'square';
    o.frequency.value = 220;
    o.connect(ctx.destination);
    o.start();
    setTimeout(() => o.stop(), 300);
  }

  // Effets sur changement de statut
  useEffect(() => {
    if (status === 'connected') playBeep();
    if (status === 'peer-left' || status === 'ended') {
      playHangup();
      setTimeout(() => {
        window.close();
        window.location.href = '/';
      }, 2000);
    }
  }, [status]);

  if (!accepted) {
    return (
      <div className="container">
        <h3>Appel entrant</h3>
        <p>Tu as un appel audio. Veux-tu décrocher ?</p>
        <div className="controls">
          <button style={{ background: 'green', color: '#fff' }} onClick={() => setAccepted(true)}>
            Décrocher
          </button>
          <button
            style={{ background: 'red', color: '#fff', marginLeft: '1rem' }}
            onClick={() => {
              window.close();
              window.location.href = '/';
            }}
          >
            Raccrocher
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="call-container">
      <div className="status">Status: {status}</div>
      <audio ref={remoteAudioRef} autoPlay />
      <div className="controls">
        <button onClick={toggleMute}>Mute/Unmute</button>
        <button onClick={hangUp}>Raccrocher</button>
      </div>
      <Chat
        messages={chatMessages}
        message={msg}
        setMessage={setMsg}
        onSend={() => {
          sendMessage(msg);
          setMsg('');
        }}
      />
    </div>
  );
}
