/* client/src/components/CallPage.jsx */
import React, { useState, useEffect } from "react";
import useWebRTC from "../hooks/useWebRTC";
import Chat from "./Chat";
import PrecallSplash from "./PrecallSplash";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import MicIcon from "@mui/icons-material/Mic";
import MicOffIcon from "@mui/icons-material/MicOff";
import CallEndIcon from "@mui/icons-material/CallEnd";

export default function CallPage({ callId }) {
  const params = new URLSearchParams(window.location.search);
  const isInitiator = params.get("initiator") === "true";
  const [splashDone, setSplashDone] = useState(isInitiator);
  const [chatOpen, setChatOpen] = useState(true);
  const [muted, setMuted] = useState(false);
  const {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    localSpeaking,
    remoteSpeaking,
    remoteMuted,
  } = useWebRTC(callId, { isInitiator });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    if (status === "connected") {
      const ctx = new (window.AudioContext || window.webkitAudioContext)();
      const osc = ctx.createOscillator();
      osc.type = "sine";
      osc.frequency.value = 440;
      osc.connect(ctx.destination);
      osc.start();
      setTimeout(() => osc.stop(), 200);
    }
  }, [status]);

  if (!splashDone) {
    return <PrecallSplash onReady={() => setSplashDone(true)} />;
  }

  return (
    <div className={`call-app dark${chatOpen ? " chat-open" : ""}`}>
      <header className="header">
        <span className="meeting-id">ID : {callId}</span>
        <div className="header-actions">
          <button className="icon-btn" onClick={() => setChatOpen((o) => !o)}>
            <ChatBubbleOutlineIcon fontSize="small" />
          </button>
        </div>
      </header>
      {isInitiator && (
        <div className="status-indicator">
          {status === "connecting"
            ? "Connexion en cours…"
            : status === "connected"
            ? "Interlocuteur connecté !"
            : null}
        </div>
      )}
      <main className="main-grid">
        <div className="video-card">
          {muted && (
            <div className="mute-indicator local">
              <MicOffIcon fontSize="small" /> Muet
            </div>
          )}
          <div className="pic-wrapper">
            <img src="/images/icon.png" alt="Vous" className="profile-pic" />
            {localSpeaking && <div className="wave" />}
          </div>
          <span className="name">Vous</span>
        </div>
        <div className="divider" />
        <div className="video-card">
          {remoteMuted && (
            <div className="mute-indicator remote">
              <MicOffIcon fontSize="small" /> Muet
            </div>
          )}
          <div className="pic-wrapper">
            <img
              src="/images/icon.png"
              alt="Interlocuteur"
              className="profile-pic"
            />
            {remoteSpeaking && <div className="wave" />}
          </div>
          <span className="name">Interlocuteur</span>
        </div>
        <aside className="chat-panel">
          <Chat
            messages={chatMessages}
            message={msg}
            setMessage={setMsg}
            onSend={() => {
              sendMessage(msg);
              setMsg("");
            }}
            isInitiator={isInitiator}
          />
        </aside>
      </main>
      <footer className="controls">
        <button
          className="icon-btn mute-btn"
          onClick={() => {
            toggleMute();
            setMuted((m) => !m);
          }}
        >
          {" "}
          {muted ? (
            <MicOffIcon fontSize="large" />
          ) : (
            <MicIcon fontSize="large" />
          )}{" "}
        </button>
        <button className="icon-btn hangup-btn" onClick={hangUp}>
          <CallEndIcon fontSize="large" />
        </button>
      </footer>
      {status === "connected" && (
        <audio ref={remoteAudioRef} autoPlay className="audio-player" />
      )}
    </div>
  );
}
