// src/hooks/useWebRTC.js
import { useEffect, useRef, useState } from "react";

export default function useWebRTC(
  callId,
  { timeout = 30000, start = true, isInitiator = false }
) {
  // --- refs & state ---
  const wsRef = useRef();
  const pcRef = useRef();
  const dataChannelRef = useRef();
  const remoteAudioRef = useRef();
  const localStreamRef = useRef();            // stream obtenu dès start
  const [status, setStatus] = useState("waiting");
  const statusRef = useRef(status);
  useEffect(() => { statusRef.current = status; }, [status]);

  const [chatMessages, setChatMessages] = useState([]);
  const [localSpeaking, setLocalSpeaking] = useState(false);
  const [remoteSpeaking, setRemoteSpeaking] = useState(false);
  const [remoteMuted, setRemoteMuted] = useState(false);

  // 1) Demande d'accès au micro dès que start = true
  useEffect(() => {
    if (!start) return;
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        localStreamRef.current = stream;
        // on peut commencer dès maintenant à détecter la parole locale
        const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
        const analyser = audioCtx.createAnalyser();
        const src = audioCtx.createMediaStreamSource(stream);
        src.connect(analyser);
        analyser.fftSize = 256;
        const dataArr = new Uint8Array(analyser.frequencyBinCount);
        (function detect() {
          analyser.getByteFrequencyData(dataArr);
          setLocalSpeaking(dataArr.reduce((a,v)=>a+v,0)/dataArr.length > 30);
          requestAnimationFrame(detect);
        })();
      })
      .catch(err => {
        console.error("Microphone access denied", err);
        // on peut définir un statut d'erreur si besoin
      });
  }, [start]);

  // 2) SIGNALING WS SETUP
  useEffect(() => {
    if (!start) return;
    const serverUrl = process.env.REACT_APP_SERVER_URL;
    wsRef.current = new WebSocket(
      `${serverUrl.replace(/^http/, "ws")}?roomId=${callId}`
    );
    wsRef.current.onopen = () => console.log("WS open");
    wsRef.current.onclose = () => console.log("WS closed");

    wsRef.current.onmessage = async ({ data }) => {
      let msg;
      try { msg = JSON.parse(data); } catch { return; }
      switch (msg.type) {
        case "room-status":
          if (msg.peers === 2 && statusRef.current === "waiting" && isInitiator) {
            await initiateCall(true);
          }
          break;
        case "offer":
          if (!pcRef.current) await initiateCall(false);
          await pcRef.current.setRemoteDescription(msg.offer);
          const answer = await pcRef.current.createAnswer();
          await pcRef.current.setLocalDescription(answer);
          wsRef.current.send(JSON.stringify({ type: "answer", answer }));
          break;
        case "answer":
          await pcRef.current.setRemoteDescription(msg.answer);
          break;
        case "candidate":
          await pcRef.current.addIceCandidate(msg.candidate);
          break;
        case "peer-left":
          setStatus("peer-left");
          break;
        case "call-ended":
          setStatus("ended");
          break;
      }
    };

    const timer = setTimeout(() => {
      if (statusRef.current !== "connected") setStatus("timeout");
    }, timeout);

    return () => {
      clearTimeout(timer);
      wsRef.current?.close();
      pcRef.current?.close();
    };
  }, [callId, start, isInitiator]);

  // 3) PEER CONNECTION SETUP
  async function initiateCall(isOfferer) {
    setStatus("connecting");
    pcRef.current = new RTCPeerConnection({
      iceServers: [{ urls: process.env.REACT_APP_STUN_SERVER }],
    });

    // on utilise le stream déjà obtenu
    if (!localStreamRef.current) {
      // sécurité : si jamais getUserMedia n’était pas prêt
      localStreamRef.current = await navigator.mediaDevices.getUserMedia({ audio: true });
    }
    localStreamRef.current.getTracks().forEach(track =>
      pcRef.current.addTrack(track, localStreamRef.current)
    );

    // handle remote track
    pcRef.current.ontrack = ({ streams: [stream] }) => {
      if (remoteAudioRef.current) {
        remoteAudioRef.current.srcObject = stream;
        remoteAudioRef.current
          .play()
          .catch(e => console.warn("Audio playback failed:", e));
      }
      // mute/unmute events
      const rt = stream.getAudioTracks()[0];
      if (rt) {
        rt.onmute = () => setRemoteMuted(true);
        rt.onunmute = () => setRemoteMuted(false);
      }
      // remote speech detection
      if (!remoteSpeaking) {
        const audioCtxR = new (window.AudioContext || window.webkitAudioContext)();
        const analyserR = audioCtxR.createAnalyser();
        const srcR = audioCtxR.createMediaStreamSource(stream);
        srcR.connect(analyserR);
        analyserR.fftSize = 256;
        const dataR = new Uint8Array(analyserR.frequencyBinCount);
        (function detect() {
          analyserR.getByteFrequencyData(dataR);
          setRemoteSpeaking(dataR.reduce((a,v)=>a+v,0)/dataR.length > 30);
          requestAnimationFrame(detect);
        })();
      }
    };

    // data channel pour chat + mute signalling
    if (isOfferer) {
      dataChannelRef.current = pcRef.current.createDataChannel("chat");
      setupDataChannel();
    } else {
      pcRef.current.ondatachannel = ({ channel }) => {
        dataChannelRef.current = channel;
        setupDataChannel();
      };
    }

    // ICE
    pcRef.current.onicecandidate = ({ candidate }) => {
      if (candidate) {
        wsRef.current.send(JSON.stringify({ type: "candidate", candidate }));
      }
    };

    // offer
    if (isOfferer) {
      const offer = await pcRef.current.createOffer();
      await pcRef.current.setLocalDescription(offer);
      wsRef.current.send(JSON.stringify({ type: "offer", offer }));
    }

    setStatus("connected");
  }

  function setupDataChannel() {
    dataChannelRef.current.onopen = () => console.log("DC open");
    dataChannelRef.current.onmessage = ({ data }) => {
      let parsed;
      try { parsed = JSON.parse(data); } catch {}
      if (parsed?.type === "mute") {
        setRemoteMuted(parsed.muted);
        return;
      }
      setChatMessages(prev => [...prev, { sender: "peer", text: data }]);
    };
  }

  // --- PUBLIC API ---
  function sendMessage(text) {
    if (dataChannelRef.current?.readyState === "open") {
      dataChannelRef.current.send(text);
      setChatMessages(prev => [...prev, { sender: "local", text }]);
    }
  }

  function toggleMute() {
    const track = localStreamRef.current?.getAudioTracks()[0];
    if (!track) return;
    track.enabled = !track.enabled;
    if (!isInitiator && dataChannelRef.current?.readyState === "open") {
      dataChannelRef.current.send(
        JSON.stringify({ type: "mute", muted: !track.enabled })
      );
    }
  }

  function hangUp() {
    if (isInitiator) {
      wsRef.current.send(JSON.stringify({ type: "end-call" }));
    }
    setStatus("ended");
    localStreamRef.current?.getTracks().forEach(t => t.stop());
    if (remoteAudioRef.current) remoteAudioRef.current.srcObject = null;
    setTimeout(() => {
      wsRef.current?.close();
      pcRef.current?.close();
    }, 100);
  }

  return {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    localSpeaking,
    remoteSpeaking,
    remoteMuted,
  };
}
