// src/components/PrecallSplash.jsx
import React, { useState, useEffect } from 'react';

export default function PrecallSplash({ onReady }) {
  const steps = [
    { label: 'Initialisation du canal sécurisé...', duration: 600 },
    { label: 'Authentification SSL/TLS...', duration: 600 },
    { label: 'Chiffrement end-to-end en cours...', duration: 600 },
    { label: 'Synchronisation des clés...', duration: 600 }
  ];
  const [phase, setPhase] = useState('invite'); // 'invite' ou 'loading'
  const [stepIndex, setStepIndex] = useState(0);

  useEffect(() => {
    let timer;
    if (phase === 'loading' && stepIndex < steps.length) {
      timer = setTimeout(() => setStepIndex(idx => idx + 1), steps[stepIndex].duration);
    } else if (phase === 'loading' && stepIndex >= steps.length) {
      timer = setTimeout(onReady, 300);
    }
    return () => clearTimeout(timer);
  }, [phase, stepIndex, onReady]);

  const handleJoin = () => {
    setPhase('loading');
    setStepIndex(0);
  };

  return (
    <div className="splash-container">
      {phase === 'invite' ? (
        <>
          <h2>Vous avez été invité·e à rejoindre une discussion privée</h2>
          <button className="join-btn" onClick={handleJoin}>Rejoindre</button>
        </>
      ) : (
        <>
          <p className="splash-step">{steps[stepIndex]?.label}</p>
          <div className="splash-loader">
            <div
              className="bar"
              style={{ width: `${(stepIndex / steps.length) * 100}%` }}
            />
          </div>
        </>
      )}
    </div>
  );
}
