// src/components/CallPage.jsx
import React, { useState } from "react";
import useWebRTC from "../hooks/useWebRTC";

export default function CallPage({ callId }) {
  const params = new URLSearchParams(window.location.search);
  const isInitiator = params.get("initiator") === "true";
  const { remoteAudioRef, status } = useWebRTC(callId, { start: true, isInitiator });

  return (
    <div style={{ textAlign: "center", padding: "40px" }}>
      <h1>Status: {status}</h1>
      <p>Link to share: {window.location.href.replace("initiator=true", "initiator=false")}</p>
      <audio ref={remoteAudioRef} autoPlay playsInline />
    </div>
  );
}
