// src/components/CallPage.jsx
import React, { useState, useEffect } from "react";
import useWebRTC from "../hooks/useWebRTC";
import Chat from "./Chat";

export default function CallPage({ callId }) {
  // Détection de l'initiator depuis l'URL ou sessionStorage
  const params = new URLSearchParams(window.location.search);
  const urlInit = params.get("initiator") === "true";
  const sessInit = sessionStorage.getItem("isInitiator") === "true";
  const isInitiator = urlInit || sessInit;

  // Si c'est l'initiator, on accepte direct, sinon on attend qu'il décroche
  const [accepted, setAccepted] = useState(isInitiator);

  const {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
  } = useWebRTC(callId, { start: accepted, timeout: 30000 });

  const [msg, setMsg] = useState("");
  const fullLink = window.location.href;

  // Sons de connexion / déconnexion
  function playBeep() {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const o = ctx.createOscillator();
    o.type = "sine";
    o.frequency.value = 440;
    o.connect(ctx.destination);
    o.start();
    setTimeout(() => o.stop(), 200);
  }
  function playHangup() {
    new Audio("/sounds/skype-hangup.mp3").play();
  }

  // Réactions aux changements de statut
  useEffect(() => {
    if (status === "connected") playBeep();
    if (status === "peer-left" || status === "ended") {
      playHangup();
      setTimeout(() => {
        window.location.href = "/";
      }, 2000);
    }
  }, [status]);

  // Écran d'appel entrant (non-initiator)
  if (!accepted) {
    return (
      <div className="call-app call-incoming">
        <header className="header">
          <h3>Appel entrant</h3>
        </header>
        <div className="incoming-actions">
          <button className="btn accept" onClick={() => setAccepted(true)}>
            📞 Décrocher
          </button>
          <button
            className="btn reject"
            onClick={() => (window.location.href = "/")}
          >
            ❌ Raccrocher
          </button>
        </div>
      </div>
    );
  }

  // UI principale
  return (
    <div className="call-app">
      <header className="header">
        <span className="meeting-id">ID de réunion : {callId}</span>
        <button
          className="copy-btn"
          onClick={() => navigator.clipboard.writeText(fullLink)}
        >
          📋
        </button>
      </header>

      <main className="main-grid">
        {/* Ta carte */}
        <div className="video-card">
          <div className="pic-wrapper">
            <img src="/images/icon.png" alt="Vous" className="profile-pic" />
            <div className="wave"></div>
          </div>
          <span className="name">Vous</span>
        </div>

        {/* Carte correspondant */}
        <div className="video-card">
          <div className="pic-wrapper">
            <img
              src="/images/icon.png"
              alt="Interlocuteur"
              className="profile-pic"
            />
            <div className="wave"></div>
          </div>
          <span className="name">Interlocuteur</span>
        </div>

        {/* Chat à droite */}
        <aside className="chat-panel">
          <Chat
            messages={chatMessages}
            message={msg}
            setMessage={setMsg}
            onSend={() => {
              sendMessage(msg);
              setMsg("");
            }}
          />
        </aside>
      </main>

      <footer className="controls">
        <button className="btn mute-btn" onClick={toggleMute}>
          🎤
        </button>
        <button className="btn hangup-btn" onClick={hangUp}>
          📞
        </button>
      </footer>

      <audio ref={remoteAudioRef} autoPlay hidden />
    </div>
  );
}
