// src/hooks/useWebRTC.js
import { useEffect, useRef, useState } from "react";

export default function useWebRTC(
  callId,
  { timeout = 30000, start = true, isInitiator = false }
) {
  const wsRef = useRef();
  const pcRef = useRef();
  const dataChannelRef = useRef();
  const remoteAudioRef = useRef();
  const localStreamRef = useRef();

  const [status, setStatus] = useState("waiting");
  const [chatMessages, setChatMessages] = useState([]);
  const [localSpeaking, setLocalSpeaking] = useState(false);
  const [remoteSpeaking, setRemoteSpeaking] = useState(false);

  useEffect(() => {
    if (!start) return;

    // 1) Ouvrir le WebSocket
    const serverUrl = process.env.REACT_APP_SERVER_URL;
    wsRef.current = new WebSocket(
      `${serverUrl.replace(/^http/, "ws")}?roomId=${callId}`
    );

    wsRef.current.onopen = () => console.log("WS open");
    wsRef.current.onclose = () => console.log("WS closed");

    // 2) Gérer les messages entrants
    wsRef.current.onmessage = async ({ data }) => {
      const text = typeof data === "string" ? data : data.toString();
      let msg;
      try {
        msg = JSON.parse(text);
      } catch {
        return; // ignorer les blobs non-JSON
      }

      switch (msg.type) {
        case "room-status":
          if (msg.peers === 2 && status === "waiting") {
            await initiateCall(isInitiator);
          }
          break;

        case "offer":
          await pcRef.current.setRemoteDescription(msg.offer);
          {
            const answer = await pcRef.current.createAnswer();
            await pcRef.current.setLocalDescription(answer);
            wsRef.current.send(JSON.stringify({ type: "answer", answer }));
          }
          break;

        case "answer":
          await pcRef.current.setRemoteDescription(msg.answer);
          break;

        case "candidate":
          await pcRef.current.addIceCandidate(msg.candidate);
          break;

        case "peer-left":
          setStatus("peer-left");
          break;

        case "call-ended":
          // L'hôte a terminé l'appel
          setStatus("ended");
          break;
      }
    };

    // 3) Fonction d'initialisation du PeerConnection
    async function initiateCall(isInitiator) {
      setStatus("connecting");
      pcRef.current = new RTCPeerConnection({
        iceServers: [{ urls: process.env.REACT_APP_STUN_SERVER }],
      });

      // --- flux audio local + détection de parole ---
      localStreamRef.current = await navigator.mediaDevices.getUserMedia({
        audio: true,
      });
      const audioCtx = new (window.AudioContext ||
        window.webkitAudioContext)();
      const analyserLocal = audioCtx.createAnalyser();
      const srcLocal = audioCtx.createMediaStreamSource(localStreamRef.current);
      srcLocal.connect(analyserLocal);
      analyserLocal.fftSize = 256;
      const dataLocal = new Uint8Array(analyserLocal.frequencyBinCount);
      function detectLocal() {
        analyserLocal.getByteFrequencyData(dataLocal);
        const avg = dataLocal.reduce((a, b) => a + b, 0) / dataLocal.length;
        setLocalSpeaking(avg > 30);
        requestAnimationFrame(detectLocal);
      }
      detectLocal();

      localStreamRef.current
        .getTracks()
        .forEach((t) => pcRef.current.addTrack(t, localStreamRef.current));

      // --- détection de parole du pair distant ---
      pcRef.current.ontrack = ({ streams: [stream] }) => {
        if (remoteAudioRef.current) remoteAudioRef.current.srcObject = stream;

        if (!remoteSpeaking) {
          const audioCtxR = new (window.AudioContext ||
            window.webkitAudioContext)();
          const analyserRemote = audioCtxR.createAnalyser();
          const srcRemote = audioCtxR.createMediaStreamSource(stream);
          srcRemote.connect(analyserRemote);
          analyserRemote.fftSize = 256;
          const dataRemote = new Uint8Array(
            analyserRemote.frequencyBinCount
          );
          function detectRemote() {
            analyserRemote.getByteFrequencyData(dataRemote);
            const avgR =
              dataRemote.reduce((a, b) => a + b, 0) / dataRemote.length;
            setRemoteSpeaking(avgR > 30);
            requestAnimationFrame(detectRemote);
          }
          detectRemote();
        }
      };

      // 4) Data channel
      if (isInitiator) {
        dataChannelRef.current = pcRef.current.createDataChannel("chat");
        setupDataChannel();
      } else {
        pcRef.current.ondatachannel = ({ channel }) => {
          dataChannelRef.current = channel;
          setupDataChannel();
        };
      }

      // 5) ICE candidates
      pcRef.current.onicecandidate = ({ candidate }) => {
        if (candidate)
          wsRef.current.send(JSON.stringify({ type: "candidate", candidate }));
      };

      // 6) Offre / réponse
      if (isInitiator) {
        const offer = await pcRef.current.createOffer();
        await pcRef.current.setLocalDescription(offer);
        wsRef.current.send(JSON.stringify({ type: "offer", offer }));
      }

      setStatus("connected");
    }

    // 7) Data channel handlers
    function setupDataChannel() {
      dataChannelRef.current.onopen = () => console.log("DataChannel open");
      dataChannelRef.current.onmessage = ({ data }) =>
        setChatMessages((prev) => [...prev, { sender: "peer", text: data }]);
    }

    // 8) Timeout de connexion
    const timer = setTimeout(() => {
      if (status !== "connected") setStatus("timeout");
    }, timeout);

    return () => {
      clearTimeout(timer);
      wsRef.current?.close();
      pcRef.current?.close();
    };
  }, [callId, start]);

  // Envoi d’un message texto
  function sendMessage(text) {
    if (dataChannelRef.current?.readyState === "open") {
      dataChannelRef.current.send(text);
      setChatMessages((prev) => [...prev, { sender: "local", text }]);
    }
  }

  // Coupure / rétablissement micro
  function toggleMute() {
    const track = localStreamRef.current?.getAudioTracks()[0];
    if (track) track.enabled = !track.enabled;
  }

  // Raccrocher
  function hangUp() {
    // 1) If host, tell everyone the call is ending
    if (isInitiator) {
      wsRef.current.send(JSON.stringify({ type: "end-call" }));
    }
    // 2) Update local status (will trigger navigation in CallPage)
    setStatus("ended");
    // 3) Stop all local tracks
    localStreamRef.current?.getTracks().forEach(t => t.stop());
    // 4) Remove the remote audio stream
    if (remoteAudioRef.current) {
      remoteAudioRef.current.srcObject = null;
    }
    // 5) Give any in-flight messages a moment, then close everything
    setTimeout(() => {
      wsRef.current?.close();
      pcRef.current?.close();
    }, 100);
  }

  return {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    localSpeaking,
    remoteSpeaking,
  };
}
