import React, { useState, useEffect } from "react";
import { useParams, useLocation } from "react-router-dom";
import useWebRTC from "../hooks/useWebRTC";
import Chat from "../components/Chat";
import PrecallSplash from "../components/PrecallSplash";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import MicIcon from "@mui/icons-material/Mic";
import MicOffIcon from "@mui/icons-material/MicOff";
import CallEndIcon from "@mui/icons-material/CallEnd";

export default function CallPageWrapper() {
  const { callId } = useParams();
  const { search } = useLocation();
  // flag initiator → sessionStorage
  const isInitiator = new URLSearchParams(search).get("initiator") === "true";
  useEffect(() => {
    if (isInitiator) sessionStorage.setItem("isInitiator", "true");
  }, [isInitiator]);

  return <CallPage callId={callId} />;
}

function CallPage({ callId }) {
  const storedInitiator = sessionStorage.getItem("isInitiator") === "true";
  const [splashDone, setSplashDone] = useState(storedInitiator);
  const [chatOpen, setChatOpen] = useState(false);
  const [muted, setMuted] = useState(false);

  const {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    localSpeaking,
    remoteSpeaking,
    remoteMuted,
  } = useWebRTC(callId, {
    start: splashDone,
    timeout: 30000,
    isInitiator: storedInitiator,
  });

  const [msg, setMsg] = useState("");

  useEffect(() => {
    if (status === "connected") {
      const ctx = new (window.AudioContext || window.webkitAudioContext)();
      const osc = ctx.createOscillator();
      osc.frequency.value = 440;
      osc.connect(ctx.destination);
      osc.start();
      setTimeout(() => osc.stop(), 200);
    }
    if (status === "peer-left" || status === "ended") {
      new Audio("/sounds/skype-hangup.mp3").play();
      if (!storedInitiator) setTimeout(() => (window.location.href = "/"), 2000);
    }
  }, [status, storedInitiator]);

  if (!splashDone) return <PrecallSplash onReady={() => setSplashDone(true)} />;

  return (
    <div className={`call-app dark${chatOpen ? " chat-open" : ""}`}>
      <header className="header">
        <span className="meeting-id">ID : {callId}</span>
        <div className="header-actions">
          <button className="icon-btn" onClick={() => setChatOpen((o) => !o)}>
            <ChatBubbleOutlineIcon fontSize="small" />
          </button>
        </div>
      </header>

      {storedInitiator && (
        <div className="status-indicator">
          {{
            waiting: "En attente de l’autre personne…",
            connecting: "Connexion en cours…",
            connected: "Interlocuteur connecté !",
            timeout: "Temps d’attente dépassé",
            "peer-left": "L’autre personne a quitté",
            ended: "Appel terminé",
          }[status]}
        </div>
      )}

      <main className="main-grid">
        {/* Local */}
        <div className="video-card">
          {muted && (
            <div className="mute-indicator local">
              <MicOffIcon fontSize="small" /> Muet
            </div>
          )}
          <div className="pic-wrapper">
            <img src="/images/icon.png" alt="Vous" className="profile-pic" />
            {localSpeaking && <div className="wave" />}
          </div>
          <span className="name">Vous</span>
        </div>

        <div className="divider" />

        {/* Remote */}
        <div className="video-card">
          {remoteMuted && (
            <div className="mute-indicator remote">
              <MicOffIcon fontSize="small" /> Muet
            </div>
          )}
          <div className="pic-wrapper">
            <img src="/images/icon.png" alt="Interlocuteur" className="profile-pic" />
            {remoteSpeaking && <div className="wave" />}
          </div>
          <span className="name">Interlocuteur</span>
        </div>

        <aside className="chat-panel">
          <Chat
            messages={chatMessages}
            message={msg}
            setMessage={setMsg}
            onSend={() => {
              sendMessage(msg);
              setMsg("");
            }}
            isInitiator={storedInitiator}
          />
        </aside>
      </main>

      <footer className="controls">
        <button
          className="icon-btn mute-btn"
          onClick={() => {
            toggleMute();
            setMuted((m) => !m);
          }}
        >
          {muted ? <MicOffIcon fontSize="large" /> : <MicIcon fontSize="large" />}
        </button>
        <button className="icon-btn hangup-btn" onClick={hangUp}>
          <CallEndIcon fontSize="large" />
        </button>
      </footer>

      {status === "connected" && (
        <audio ref={remoteAudioRef} autoPlay className="audio-player" />
      )}
    </div>
  );
}
