// src/components/CallPage.jsx
import React, { useState, useEffect } from 'react';
import useWebRTC from '../hooks/useWebRTC';
import Chat from './Chat';
import PrecallSplash from './PrecallSplash';

import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import MicIcon from '@mui/icons-material/Mic';
import MicOffIcon from '@mui/icons-material/MicOff';
import CallEndIcon from '@mui/icons-material/CallEnd';

export default function CallPage({ callId }) {
  // initiator flag
  const params = new URLSearchParams(window.location.search);
  const urlInit = params.get('initiator') === 'true';
  const sessInit = sessionStorage.getItem('isInitiator') === 'true';
  const isInitiator = urlInit || sessInit;

  // Splash init pour non-initiator
  const [splashDone, setSplashDone] = useState(isInitiator);
  const [accepted, setAccepted] = useState(isInitiator);
  const [chatOpen, setChatOpen] = useState(false);
  const [muted, setMuted] = useState(false);

  const {
    remoteAudioRef,
    status,
    chatMessages,
    sendMessage,
    toggleMute,
    hangUp,
    localSpeaking,
    remoteSpeaking,
  } = useWebRTC(callId, { start: accepted, timeout: 30000, isInitiator });

  const [msg, setMsg] = useState('');
  const fullLink = window.location.href;

  // Son de notification déjà joué dans l’effet; on garde playBeep sur 'connected'
  useEffect(() => {
    if (status === 'peer-left' || status === 'ended') {
      new Audio('/sounds/skype-hangup.mp3').play();
      setTimeout(() => (window.location.href = '/'), 2000);
    }
  }, [status]);

  const handleMute = () => {
    toggleMute();
    setMuted(m => !m);
  };

  // 1) Splash pré-appel
  if (!splashDone) {
    return <PrecallSplash onReady={() => setSplashDone(true)} />;
  }

  // 2) Écran appel entrant
  if (!accepted) {
    return (
      <div className="call-app incoming dark">
        <header className="header">
          <h3>Appel entrant</h3>
        </header>
        <div className="incoming-actions">
          <button className="btn accept" onClick={() => setAccepted(true)}>
            Accepter
          </button>
          <button className="btn reject" onClick={() => (window.location.href = '/')}>
            Refuser
          </button>
        </div>
      </div>
    );
  }

  // 3) UI principale
  return (
    <div className={`call-app dark${chatOpen ? ' chat-open' : ''}`}>
      <header className="header">
        <span className="meeting-id">{callId}</span>
        <div className="header-actions">
          <button className="icon-btn" onClick={() => navigator.clipboard.writeText(fullLink)}>
            <ContentCopyIcon fontSize="large" />
          </button>
          <button className="icon-btn" onClick={() => setChatOpen(o => !o)}>
            <ChatBubbleOutlineIcon fontSize="large" />
          </button>
        </div>
      </header>

      {/* Indicateur de statut */}
      <div className="status-indicator">
        {status === 'waiting' && 'En attente de l’autre personne...'}
        {status === 'connecting' && 'Connexion en cours...'}
        {status === 'connected' && 'Interlocuteur connecté !'}
        {status === 'timeout' && 'Temps d’attente dépassé'}
        {status === 'peer-left' && 'L’autre personne a quitté l’appel'}
        {status === 'ended' && 'Appel terminé'}
      </div>

      <main className="main-grid">
        <div className="video-card">
          <div className="pic-wrapper">
            <img src="/images/icon.png" alt="Vous" className="profile-pic" />
            {localSpeaking && <div className="wave"></div>}
          </div>
          <span className="name">Vous</span>
        </div>

        <div className="divider" />

        <div className="video-card">
          <div className="pic-wrapper">
            <img src="/images/icon.png" alt="Interlocuteur" className="profile-pic" />
            {remoteSpeaking && <div className="wave"></div>}
          </div>
          <span className="name">Interlocuteur</span>
        </div>

        <aside className="chat-panel">
          <div className="chat-container">
            <Chat
              messages={chatMessages}
              message={msg}
              setMessage={setMsg}
              onSend={() => {
                sendMessage(msg);
                setMsg('');
              }}
            />
          </div>
        </aside>
      </main>

      <footer className="controls">
        <button className="icon-btn mute-btn" onClick={handleMute}>
          {muted ? <MicOffIcon fontSize="large" /> : <MicIcon fontSize="large" />}
        </button>
        <button className="icon-btn hangup-btn" onClick={hangUp}>
          <CallEndIcon fontSize="large" />
        </button>
      </footer>

      <audio ref={remoteAudioRef} autoPlay hidden className="audio-player" />
    </div>
  );
}
